SELECT HOTEL_NAME, ORIGINAL_PRICE
FROM SCRAPED_DATA
WHERE TRUNC(SCRAPED_DATETIME) = TRUNC(SYSDATE - 1);

SELECT LOCATION, AVG(ORIGINAL_PRICE) AS avg_price
FROM SCRAPED_DATA
GROUP BY LOCATION;

SELECT HOTEL_NAME, MIN(ORIGINAL_PRICE) AS min_price
FROM SCRAPED_DATA
GROUP BY HOTEL_NAME;

SELECT LOCATION, AVG(ORIGINAL_PRICE) AS avg_price
FROM SCRAPED_DATA
GROUP BY LOCATION;
